def solve(tuple_list):
    flatten = [item for sublist in tuple_list for item in sublist]
    return ''.join(flatten)

def solve(tup):
    result = ""
    for i in tup:
        if type(i) == tuple:
            result += solve(i)
        else:
            result += str(i) + " "
    return result

# Example
tup = (1, 2, (3, 4), (5, (6, 7)))
print(solve(tup))
# Output: 1 2 3 4 5 6 7

def solve(tuple_list):
    flat_list = []
