        flat_list.extend(tup)
    return ''.join(flat_list)

def solve(tuple_list):
    flatten_tuple = []
    for tup in tuple_list:
        for item in tup:
            flatten_tuple.append(item)
    return ''.join(flatten_tuple)

def solve(tuple_list):
