def solve(n):
    sum = 0
    for i in range(n+1):
        if i == n:
            break
        else:
            diff = bin(i)[2:].zfill(len(bin(n)[2:])) #convert numbers to binary and pad them with zeros to have equal length
            next_diff = bin(i+1)[2:].zfill(len(bin(n)[2:]))
            for j in range(len(diff)):
                if diff[j] != next_diff[j]:
                    sum += 1
    return sum

