def solve(lst):
    result = []
    for i in range(len(lst)):
        temp = [lst[i][0], lst[i][-1]]
        result.append(temp)
    return result

def solve(list_of_lists):
    merged = []
    for lst in list_of_lists:
        if len(lst) > 1:
            merged.append([lst[0], lst[-1]])
        else:
            merged.append(lst)
    return merged
