def solve(arr):
    is_monotonic = True
    increasing = True
    decreasing = True

    for i in range(1, len(arr)):
        if arr[i] > arr[i-1]:
            decreasing = False
        elif arr[i] < arr[i-1]:
            increasing = False

    if increasing or decreasing:
        return is_monotonic
    else:
        return not is_monotonic
